6 */ fun setHashOfPin(p : Int): String { val h = Hashing() val hash : String = h.sha256(p) return hash } /** * Gets the hash of the pib of customer supplied * @return String Hash of Pin */ fun getHashOfPin(): String? { return this.pinHash } /** * Get account number * @return Int accNumber */ fun getAccount() : Int? { return this.accNumber } /** * Get sort number * @return String sortCode */ fun getSortCode() : String? { return this.sortCode } /** * Retrieves the current customers details in a nice format * @return String Customer Information */ fun customerToString() : String { return ("\n Sort Code: ${this.sortCode}\n Acc No:${this.accNumber}\n Name: ${this.firstName} ${this.lastName}\n Balance: £${this.balance}\n" ); } /** * Creates an account number using repeat and random. * * Format: xxxxxxxx */ fun makeAccountNumber() { var tmpAccNumber : String? = null repeat(8){ var tmpNum = Random.nextInt(0, 9) // Avoid 0 as the first number. while (it == 0 && tmpNum == 0) { tmpNum = Random.nextInt(0, 9) } if(it == 0) { tmpAccNumber = tmpNum.toString() } else { tmpAccNumber += tmpNum.toString() } } this.accNumber = tmpAccNumber?.toInt() } /** * Creates an account number using repeat and random. * * Format: xx-xx-xx */ fun makeSortCode() { var tmpSortCode : String = "" repeat(6){ if (it != 0 && it % 2 == 0) { tmpSortCode += '-' } tmpSortCode += Random.nextInt(0, 6).toString() } this.sortCode = tmpSortCode }
