} Full code: FundsController.kt class FundsController { private val e = ErrorMessage() /** * With draw provide amount does not put the balance below 0. * * Will set account balance = balance - withdrawal * @param Object BankCustomer * @param Double withdrawal * @throws Exception balance less than zero **/ fun withdrawal(bc: BankCustomer, withdrawal : Double) { if(withdrawal <= 0.00) { e.errorMessage("Withdrawal amount must be greater than 0.00") } if(bc.balance == 0.00) { e.errorMessage("Insufficient funds. Current Balance is: ${bc.balance}") } else if((bc.balance?.minus(withdrawal))!! >= 0.00) { bc.setBalance(bc.balance?.minus(withdrawal)!!) } else { e.errorMessage("Balance will be less than 0.00") } } /** * Deposits the provided amount into the account. * @param Object BankCustomer * @param Double deposit * @throws Exception deposit less than zero **/ fun deposit(bc: BankCustomer, amount : Double) { if(amount <= 0.00) { e.errorMessage("Amount being deposit must be greater than 0.00") } else { bc.setBalance(bc.balance!!.plus(amount)) } } /** * Transfers amount from one acoount to another * @param Object fromAcc * @param Object toAcc * @param Double amount * @throws insufficient funds and if amount is less than 0.00 **/ fun transfer(fromAcc: BankCustomer,toAcc: BankCustomer,amount: Double) { if(amount <= 0.00) { e.errorMessage("Amount being deposit must be greater than 0.00") } else if(fromAcc.balance == 0.00) { e.errorMessage("Insufficient funds. Current Balance is: ${fromAcc.balance}") } else if(fromAcc.balance!!.minus(amount) >= 0.00) { fromAcc.setBalance(fromAcc.balance!!.minus(amount)) toAcc.setBalance(toAcc.balance!!.plus(amount)) } else { e.errorMessage("Transfer cancelled balance will be less than 0.00") } }
